<template>
  <img
    src="data:image/png;base64,"
    alt="指南针"
    class="d2c-compass"
    @click="map.setBearing(0)"
    :style="rotateStyle"
  />
</template>

<script>
export default {
  name: 'd2c-compass',
  props: {
    map: {
      default() {
        return null;
      },
    },
  },
  computed: {
    rotateStyle() {
      if (!this.map) {
        return {};
      }
      return {
        transform: `rotate(${-this.map.getBearing()}deg)`,
        '-ms-transform': `rotate(${-this.map.getBearing()}deg)` /* IE 9 */,
        '-moz-transform': `rotate(${-this.map.getBearing()}deg)` /* Firefox */,
        '-webkit-transform': `rotate(${-this.map.getBearing()}deg)` /* Safari 和 Chrome */,
        '-o-transform': `rotate(${-this.map.getBearing()}deg)`,
      };
    },
  },
};
</script>
<style lang="scss" scoped>
.d2c-compass {
  cursor: pointer;
  position: absolute;
  bottom: 10px;
  right: 10px;
}
</style>
